import{_ as t,d as s,o,e}from"./chunks/framework.By5mSbJE.js";const n="/assets/circles.9ndqlcE5.svg",x=JSON.parse('{"title":"Chromatone","description":"Визуальный музыкальный язык и средства для его изучения и использования.","frontmatter":{"title":"Chromatone","description":"Визуальный музыкальный язык и средства для его изучения и использования.","url":"https://chromatone.center","date":"2017-12-02T00:00:00.000Z","status":"process","icon":"circle-of-fifths.svg","list":"chromatone","type":"design"},"headers":[],"relativePath":"designs/chromatone/index.md","filePath":"designs/chromatone/index.md"}'),c={name:"designs/chromatone/index.md"},a=e("p",null,"И звук и свет — волновые явления и у них очень много общего. И это не только физические свойства, но и их влияние на сознание людей. Приглушённые цвета, яркие нотки — ум человека собирает разрозненные сигналы чувств в единый образ. Это значит, что для нас цвет и звук связаны неразрывно всегда.",-1),r=e("p",null,[e("img",{src:n,alt:""})],-1),i=e("p",null,"История знает множество подходов к темперации звукоряда. Современный равномерно темперированный строй делит весь спектр музыкальных звуков на 9 октав по 12 нот в каждой. Сопоставляя ноты октавы 12 основным цветам спектра света, мы обнаружили мостик между визуальной и аудиальной системами, позволяющий лучше разглядеть принципы музыкальной гармонии и созвучности цветов. 9 октав по 12 нот — 108 базовых частот, сочетая которые порождается бескрайнее музыкальное пространство.",-1),d=[a,r,i];function l(_,p,m,h,f,u){return o(),s("div",null,d)}const v=t(c,[["render",l]]);export{x as __pageData,v as default};
