import{_ as t,o as s,c as o,a as e}from"./chunks/framework.58be8ee7.js";const n="/assets/vegan-01.33c263fe.png",B=JSON.parse('{"title":"Клуб экологичного питания","description":"Здоровый подход к рациональному растительному питанию","frontmatter":{"title":"Клуб экологичного питания","description":"Здоровый подход к рациональному растительному питанию","status":"development","icon":"efc.svg","date":"2014-08-05T00:00:00.000Z","type":"design"},"headers":[],"relativePath":"designs/ecofoodclub/index.md","filePath":"designs/ecofoodclub/index.md"}'),c={name:"designs/ecofoodclub/index.md"},d=e("p",null,"Задавшись целью найти оптимальный способ питания человека в городе, находишь растительную пищу наиболее естественной. Как с точки зрения здоровья и этики, так и экологии природы и общества. Решив глубоко разобраться с вопросами питания, каждый приходит к веганству своим собственным путём. И это не усложнение жизни — современное общество способно полноценно обеспечить любой тип питания. Доступность информации, продуктов и способов приготовления позволяет сделать растительный рацион полноценным как по вкусовому разнообразию, так и по питательной ценности. А прекрасное самочувствие и свобода от соучастия в насилии над животными дают человеку основания считать такой выбор для себя окончательным.",-1),i=e("p",null,[e("img",{src:n,alt:""})],-1),a=e("p",null,"Несмотря на широкий выбор самых разнообразных продуктов, веган в городе в значительной степени ограничен в вопросах питания. Как часто нам не хватает времени или умений приготовить для себя вкусной и полезной пищи? Кафе и рестораны, претендуя на полноценность и разнообразие блюд, оказываются дорогим удовольствием для каждого, кто не готов постоянно оплачивать услуги профессиональных поваров и официантов. Столовые, отчасти, восполняют этот пробел, как и различные уличные сети питания. Курьерские службы помогают сэкономить ещё немного времени.",-1),l=e("p",null,"Но есть радикальное решение — научиться легко и быстро готовить себе еду самостоятельно. Осознанно выбирать продукты и комбинировать их в различных сочетаниях. Правда, вот так взять, выбежать после рабочего дня с места и поспешить домой, чтобы успеть приготовить себе ужин — достойная цель, но в реалиях густо населённого мегаполиса далеко не каждый сумеет добраться до своей кухни, буквально, не озверев от голода. Замороженные полуфабрикаты и блюда быстрого приготовления нередко становятся основным рационом такого самостоятельного горожанина. И не потому, что жена не приготовила. Женщины завоевали себе свободу от кухонного рабства и не стоит пытаться заковать их в эти древние цепи назад — настало время искать новые решения давних проблем.",-1),_=e("p",null,"Развитие Интернета и социальных сетей значительно облегчили поиск рецептов и обмен кулинарным опытом и сегодня каждый может без труда освоить приготовление множества разнообразных блюд. Привычка постоянно учиться, осваивая всё новые интерфейсы и среды коммуникации, стало неотъемлемым качеством молодых горожан и можно быть уверенными в том, что общество может принять и освоить новые способы приготовления и потребления пищи.",-1),p=e("p",null,"Просторная профессиональная кухня со всем необходимым оборудованием с прозрачной системой бронирования и использования, совместно пополняемый запас продуктов, витрина и система маркировки разнообразных готовых блюд и зал, удобный для принятия пищи — перспективная среда для улучшения рациона питания и коммуникации горожан. Самообслуживание, доведённое до кульминации — полностью общественный цикл производства и потребления пищи, включая поставки продуктов и мытьё посуды.",-1),r=e("p",null,"Мы разрабатываем прозрачную систему совместного питания, где каждый человек сможет найти себе подходящую роль и включиться в жизнь сообщества. Обмен рецептами, кулинарные мастер-классы, совместное приготовление блюд, возможность поделиться лишней порцией с окружающими — вот только первые плюсы такого подхода. Клубы экологичного питания в городах станут местами кипящей социальной активности, где самые разные люди регулярно встречаются, готовят и едят вместе самые разнообразные растительные блюда.",-1),u=[d,i,a,l,_,p,r];function f(h,m,g,x,v,$){return s(),o("div",null,u)}const k=t(c,[["render",f]]);export{B as __pageData,k as default};
