import{_ as e,o as t,c as a,d as i}from"./chunks/framework.bfe12c2a.js";const v=JSON.parse('{"title":"Онлайн-словарь новых слов","description":"Веб-приложение для сбора и обзора придуманных в ходе игры слов","frontmatter":{"title":"Онлайн-словарь новых слов","description":"Веб-приложение для сбора и обзора придуманных в ходе игры слов","status":"development","cover":"canvas.png","date":"2019-04-02T00:00:00.000Z","list":"etovoteto-app","type":"project"},"headers":[],"relativePath":"designs/etovoteto/app/index.md","filePath":"designs/etovoteto/app/index.md"}'),o={name:"designs/etovoteto/app/index.md"},p=i('<p>Интерактивная среда для игры в слова. Приложение ориентировано в первую очередь на заполнение и изучение с мобильных устройств в процессе игры с друзьями или в качестве веселого досуга.</p><p>Возможности авторов онлайн-словаря этовотэто:</p><ul><li>добавлять придуманные слова и определения</li><li>связывать слова и определения друг с другом</li><li>иметь свой счет добавленных слов и определений</li><li>наполнить профиль информацией о себе</li><li>создавать вложенные тематические комнаты</li><li>переходить между карточками, делиться лучшими с друзьями</li></ul><p>Разработка интерактивного словаря – непростой вызов для фронтендера, т.к. здесь не обойтись без общей базы данных, где будут собираться придуманные слова и определения к ним.</p><p>Все началось с PHP системы построенной на API <a href="./directus/">Directus CMS</a> и с интерфейсом на VueJS. Система работала вполне неплохо. Но в какой-то момент я не смог следовать за обновлениями этой системы (она перешла на другую базу данных, а затем сменила платформу на JavaScript). А меня все больше манила разработка сервисов с использованием децентрализованной базы данных GUN.</p><p>Во <a href="./vuetify-gun/">второй итерации словаря придуманных слов</a> на основе базы данных GUN и UI-фреймфорка Vuetify получилось создать первую децентрализованную версию приложения. Но лишь с <a href="./v3/">третьего подхода</a> мне удалось включить всю мощь децентрализации и криптографии. Слова и определения хранятся под из хешами, что гарантирует неизменность данных, а пользовательские связи между словами подписываются асимметричными ключами шифрования.</p><p>Некоторые проблемы с парсингом неизменяемых данных в доступной тогда версии GUN породили массу проблем с производительностью системы и несмотря на попытки запустить её продвижение, пользователей в этой версии практически не было. Но масса опыта и ценных наработок в плане алгоритмов не пропала зря и теперь активно внедряется в разработке более универсального и международного проекта <a href="./../../defucc/gun-vue/">Gun-Vue</a></p>',7),s=[p];function n(r,c,_,d,l,u){return t(),a("div",null,s)}const h=e(o,[["render",n]]);export{v as __pageData,h as default};
