import{_ as s,o as t,c as n,a as e}from"./chunks/framework.bfe12c2a.js";const o="/assets/feeds.e14e009d.svg",y=JSON.parse('{"title":"Ленты","description":"Интерфейс совместной разработки и реализации проектов","frontmatter":{"title":"Ленты","description":"Интерфейс совместной разработки и реализации проектов","icon":"feeds.svg","url":"https://feeds.frkt.ru","date":"2013-12-20T00:00:00.000Z","status":"development","type":"design","list":"feeds"},"headers":[],"relativePath":"designs/feeds/index.md","filePath":"designs/feeds/index.md"}'),d={name:"designs/feeds/index.md"},i=e("p",null,[e("img",{src:o,alt:""})],-1),l=e("p",null,"Современные веб-сайты представляют множество вариантов организации потоков информации, в том числе и платежной. Статические сайты, социальные сети, блоги, форумы, и множество различных менее обобщаемых форматов. Все это многообразие позволяет направлять бесчисленные потоки информации. Социальные сети доказали свою значимость для общества в процессе быстрого и глубокого проникновения их в социальные взаимодействия. Возможности создания виртуальных представительств групп людей или событий чрезвычайно расширяют возможности влияния соц. сетей на происходящее в оффлайне. Наблюдается и обратная тенденция с растворением в виртуальном пространстве множества видов деятельности еще недавно не требовавших даже наличия компьютера.",-1),_=e("p",null,"Возникшая и стремительно развивающаяся сфера интернет-торговли обнаруживает уже куда большее влияние на окружающий мир, хоть и по старым правилам. Новые правила направления движения средств, продукции и человеческих ресурсов внесли в общество различные краудфандинговые и краудсорсинговые площадки.",-1),c=e("p",null,"В результате наблюдения за ними все более очевидными становятся преимущества организации социального взаимодействия на основании правильно сформулированных проектов. Понятие правильного проекта меняется от площадки к площадке, но каркас у них общий. Для успешной реализации любого проекта нужно определиться с его названием, коротким, но емким описанием, датами начала и окончания той или иной деятельности в рамках проекта, описания необходимых для реализации средств, а также списка подарков, которые может получить каждый, кто совершит определенный вклад в этот проект.",-1),a=e("p",null,"Таким образом и возникает идея о возможности построения принципиально нового способа конструктивного общественного взаимодействия посредством сети, направленного на открытую совместную разработку и реализацию различных проектов. Используя продуманную созидательно-направленную систему взаимодействия лиц, возможно раскрыть потенциал конструктивного взаимодействия людей в сети.",-1),r=e("hr",null,null,-1),p=e("p",null,"Суть проекта в создании модульного веб-приложения, которое будет позволять своим пользователям проявлять максимум своих офф-лайн возможностей, отходить или откладывать в сторону экраны и что-то делать по-настоящему. Учитывая важность прозрачности вложения и направления средств и усилий, необходимо обеспечить возможности коллективной оценки затей, а также сбора и направления средств на их реализацию.",-1),u=e("p",null,"В настоящее время многочисленные исследования в области UX показывают удобство и психологическую эргономичность интерфейсов построенных на использовании модульной системы виртуальных карточек, содержащих ту или иную информацию, а также наглядной и конструктивной системы организации их взаимосвязей.",-1),h=e("p",null,"Интерактивный прототип, позволяет на практике испытать эффективность “электронной картотеки” с широкими возможностями поиска, сбора и коллективного структурирования информации.",-1),f=e("p",null,"Разработка прозрачной системы сбора даров и направления их на реализацию затей позволит обеспечить проекты необходимыми средствами. Прозрачная система отчетности о расходовании средств участниками реализации проектов обеспечит устойчивость системы к недобросовестности отдельных лиц. Таким образом Ленты — важная часть большого и сложного организма Фонда ФРУКТ, потенциально позволяющая осуществлять все его функции в сети.",-1),m=[i,l,_,c,a,r,p,u,h,f];function g(x,v,$,k,B,N){return t(),n("div",null,m)}const D=s(d,[["render",g]]);export{y as __pageData,D as default};
