import{_ as t,d as c,o as s,e}from"./chunks/framework.By5mSbJE.js";const a="data:image/svg+xml,%3csvg%20id='Layer_1'%20data-name='Layer%201'%20xmlns='http://www.w3.org/2000/svg'%20viewBox='0%200%20283.46%20283.46'%3e%3cdefs%3e%3cstyle%3e%20.cls-1%20{%20opacity:%200.75;%20}%20.cls-2%20{%20fill:%20%23004c4d;%20}%20.cls-3%20{%20fill:%20%2301999a;%20}%20%3c/style%3e%3c/defs%3e%3ctitle%3eFeeds%3c/title%3e%3cg%20id='_Group_'%20data-name='&lt;Group&gt;'%3e%3cg%20class='cls-1'%3e%3cg%20id='_Group_2'%20data-name='&lt;Group&gt;'%3e%3cpath%20class='cls-2'%20d='M40.17,159.63H28V139l13.57-12.32h22.7v33H52.18v-22h-12Z'/%3e%3c/g%3e%3cg%20id='_Group_3'%20data-name='&lt;Group&gt;'%3e%3cpath%20class='cls-2'%20d='M103.33,148.62v11H75.44L67.06,152V139l13.57-12.32h22.7v14.26l-8.26,7.69ZM91.2,137.67h-12v10.95h12Z'/%3e%3c/g%3e%3cg%20id='_Group_4'%20data-name='&lt;Group&gt;'%3e%3cpath%20class='cls-2'%20d='M143,126.67v33H130.85V144.37L126,148.74h-7.19v10.88H106.71v-33h12.13v11h12v-11Z'/%3e%3c/g%3e%3cg%20id='_Group_5'%20data-name='&lt;Group&gt;'%3e%3cpath%20class='cls-2'%20d='M158.49,126.67v7.69l8.44-7.69h13.45v7.69l8.44-7.69h13.44v33H190.13v-22h-9.75v22H168.24v-22h-9.76v22H146.36v-33Z'/%3e%3c/g%3e%3cg%20id='_Group_6'%20data-name='&lt;Group&gt;'%3e%3cpath%20class='cls-2'%20d='M217.77,138.49l6.88-6.32h17.26v15.07L228.4,159.62H205.64v-33h12.13Zm0,1.94v8.19h10.94v-8.19Zm37.64,19.2H243.29v-33h12.13v33Z'/%3e%3c/g%3e%3c/g%3e%3cg%3e%3cg%20id='_Group_7'%20data-name='&lt;Group&gt;'%3e%3cg%20id='_Group_8'%20data-name='&lt;Group&gt;'%3e%3cpath%20class='cls-3'%20d='M40.17,156.79H28V136.15l13.57-12.32h22.7v33H52.18v-22h-12Z'/%3e%3c/g%3e%3c/g%3e%3cg%20id='_Group_9'%20data-name='&lt;Group&gt;'%3e%3cg%20id='_Group_10'%20data-name='&lt;Group&gt;'%3e%3cpath%20class='cls-3'%20d='M103.33,145.79v11H75.44l-8.38-7.63v-13l13.57-12.32h22.7v14.26l-8.26,7.69ZM91.2,134.84h-12v10.95h12Z'/%3e%3c/g%3e%3c/g%3e%3cg%20id='_Group_11'%20data-name='&lt;Group&gt;'%3e%3cg%20id='_Group_12'%20data-name='&lt;Group&gt;'%3e%3cpath%20class='cls-3'%20d='M143,123.83v33H130.85V141.53L126,145.91h-7.19v10.88H106.71v-33h12.13v11h12v-11Z'/%3e%3c/g%3e%3c/g%3e%3cg%20id='_Group_13'%20data-name='&lt;Group&gt;'%3e%3cg%20id='_Group_14'%20data-name='&lt;Group&gt;'%3e%3cpath%20class='cls-3'%20d='M158.49,123.83v7.69l8.44-7.69h13.45v7.69l8.44-7.69h13.44v33H190.13v-22h-9.75v22H168.24v-22h-9.76v22H146.36v-33Z'/%3e%3c/g%3e%3c/g%3e%3cg%20id='_Group_15'%20data-name='&lt;Group&gt;'%3e%3cg%20id='_Group_16'%20data-name='&lt;Group&gt;'%3e%3cpath%20class='cls-3'%20d='M217.77,135.65l6.88-6.32h17.26v15.07L228.4,156.79H205.64v-33h12.13Zm0,1.94v8.19h10.94v-8.19Zm37.64,19.2H243.29v-33h12.13v33Z'/%3e%3c/g%3e%3c/g%3e%3c/g%3e%3c/g%3e%3c/svg%3e",y=JSON.parse('{"title":"Ленты","description":"Интерфейс совместной разработки и реализации проектов","frontmatter":{"title":"Ленты","description":"Интерфейс совместной разработки и реализации проектов","icon":"feeds.svg","url":"https://feeds.frkt.ru","date":"2013-12-20T00:00:00.000Z","status":"development","type":"design","list":"feeds"},"headers":[],"relativePath":"designs/feeds/index.md","filePath":"designs/feeds/index.md"}'),l={name:"designs/feeds/index.md"},d=e("p",null,[e("img",{src:a,alt:""})],-1),o=e("p",null,"Современные веб-сайты представляют множество вариантов организации потоков информации, в том числе и платежной. Статические сайты, социальные сети, блоги, форумы, и множество различных менее обобщаемых форматов. Все это многообразие позволяет направлять бесчисленные потоки информации. Социальные сети доказали свою значимость для общества в процессе быстрого и глубокого проникновения их в социальные взаимодействия. Возможности создания виртуальных представительств групп людей или событий чрезвычайно расширяют возможности влияния соц. сетей на происходящее в оффлайне. Наблюдается и обратная тенденция с растворением в виртуальном пространстве множества видов деятельности еще недавно не требовавших даже наличия компьютера.",-1),p=e("p",null,"Возникшая и стремительно развивающаяся сфера интернет-торговли обнаруживает уже куда большее влияние на окружающий мир, хоть и по старым правилам. Новые правила направления движения средств, продукции и человеческих ресурсов внесли в общество различные краудфандинговые и краудсорсинговые площадки.",-1),n=e("p",null,"В результате наблюдения за ними все более очевидными становятся преимущества организации социального взаимодействия на основании правильно сформулированных проектов. Понятие правильного проекта меняется от площадки к площадке, но каркас у них общий. Для успешной реализации любого проекта нужно определиться с его названием, коротким, но емким описанием, датами начала и окончания той или иной деятельности в рамках проекта, описания необходимых для реализации средств, а также списка подарков, которые может получить каждый, кто совершит определенный вклад в этот проект.",-1),_=e("p",null,"Таким образом и возникает идея о возможности построения принципиально нового способа конструктивного общественного взаимодействия посредством сети, направленного на открытую совместную разработку и реализацию различных проектов. Используя продуманную созидательно-направленную систему взаимодействия лиц, возможно раскрыть потенциал конструктивного взаимодействия людей в сети.",-1),g=e("hr",null,null,-1),r=e("p",null,"Суть проекта в создании модульного веб-приложения, которое будет позволять своим пользователям проявлять максимум своих офф-лайн возможностей, отходить или откладывать в сторону экраны и что-то делать по-настоящему. Учитывая важность прозрачности вложения и направления средств и усилий, необходимо обеспечить возможности коллективной оценки затей, а также сбора и направления средств на их реализацию.",-1),h=e("p",null,"В настоящее время многочисленные исследования в области UX показывают удобство и психологическую эргономичность интерфейсов построенных на использовании модульной системы виртуальных карточек, содержащих ту или иную информацию, а также наглядной и конструктивной системы организации их взаимосвязей.",-1),i=e("p",null,"Интерактивный прототип, позволяет на практике испытать эффективность “электронной картотеки” с широкими возможностями поиска, сбора и коллективного структурирования информации.",-1),v=e("p",null,"Разработка прозрачной системы сбора даров и направления их на реализацию затей позволит обеспечить проекты необходимыми средствами. Прозрачная система отчетности о расходовании средств участниками реализации проектов обеспечит устойчивость системы к недобросовестности отдельных лиц. Таким образом Ленты — важная часть большого и сложного организма Фонда ФРУКТ, потенциально позволяющая осуществлять все его функции в сети.",-1),u=[d,o,p,n,_,g,r,h,i,v];function m(G,H,f,Z,M,x){return s(),c("div",null,u)}const V=t(l,[["render",m]]);export{y as __pageData,V as default};
