import{_ as s,d as e,o as a,K as n}from"./chunks/framework.wH9i19Ac.js";const i="/assets/ILCA4.lQGS2k2N.png",o="/assets/ILCA-2.DC0D49yX.svg",r="/assets/hand.DcPNDbO7.svg",c="/assets/time.DmlvDDZ5.svg",p="/assets/notes.BATBsI1T.svg",l="/assets/cal.m9UCBj9x.svg",x=JSON.parse('{"title":"Международный буквенный счёт и учёт","description":"Универсальная система учёта независимых сущностей, свободная от цифр и вносимых ими искажений восприятия.","frontmatter":{"title":"Международный буквенный счёт и учёт","description":"Универсальная система учёта независимых сущностей, свободная от цифр и вносимых ими искажений восприятия.","icon":"ilca.svg","url":"https://frkt.ru/ilca/","status":"development","date":"2014-01-24T00:00:00.000Z","type":"design","list":"ilca"},"headers":[],"relativePath":"designs/ilca/index.md","filePath":"designs/ilca/index.md"}'),_={name:"designs/ilca/index.md"};function d(m,t,g,u,f,h){return a(),e("div",null,t[0]||(t[0]=[n('<h2 id="international-letter-count-and-account" tabindex="-1">International Letter Count and Account <a class="header-anchor" href="#international-letter-count-and-account" aria-label="Permalink to &quot;International Letter Count and Account&quot;">​</a></h2><p>Десятичная система счисления кажется единственной, но это далеко не так. Высокоразвитые цифровые технологии, например, основаны на двоичной системе. Не забыто слово “дюжина” и не даром во многих языках существуют отдельные слова для цифр 11 и 12. Оказывается, во многих сферах жизни можно столкнуться с двенадцатеричной системой.</p><p>На циферблате 12 часов, в году 12 месяцев. Музыкальных нот, оказывается, тоже 12. Видимый солнечный спектр тоже можно разбить на 12 частей. Сохранились знания о древней системе счёта на фалангах пальцев в двенадцатеричной системе. Священное число индуизма — 108=12х9. 12 имеет множество делителей: 2, 3, 4, 6 — очень удобное для устного счёта свойство.</p><p>Десятичные цифры позволяют легко производить математические и счётные операции и хорошо годятся для учёта однотипных сущностей. Цифры выстраиваются в строгую последовательность и не терпят перестановок. Цифры можно заменить.</p><p>Кириллический и латинский алфавиты объединяют ровно 12 литер. Эти буквы уже давно используются для маркировки автомобильных номеров, например. Мы выстроили их в алфавитном порядке, сопоставили им цифры, цвета, месяцы и сформировали подходящие правила счёта и учёта в нестрогой последовательности.</p><p><img src="'+i+'" alt=""></p><hr><p>В отличие от десятичных чисел, где каждый новый разряд заполняется в строгой последовательности от 1 до 9, от 10 до 99 и т.д, разряды буквенного учёта могут заполняться случайным образом, а сочетания букв можно выбирать для каждой сущности отдельно.</p><p><img src="'+o+'" alt=""></p><p><img src="'+r+'" alt=""></p><p><img src="'+c+'" alt=""></p><p><img src="'+p+'" alt=""></p><p><img src="'+l+'" alt=""></p>',13)]))}const D=s(_,[["render",d]]);export{x as __pageData,D as default};
