import{_ as t,d as o,o as s,e,J as n}from"./chunks/framework.By5mSbJE.js";const g=JSON.parse('{"title":"Проектный подход","description":"Обобщенная система реализации любых творческих начинаний","frontmatter":{"title":"Проектный подход","description":"Обобщенная система реализации любых творческих начинаний","icon":"wheel.png","url":"https://ooley.ru/projectapp","opportunities":[{"verb":"Изучить","text":"простой метод разделения сложных творческих задач на простые выполнимые этапы","link":"https://ooley.ru/projectapp/"},{"verb":"Пригласить","text":"команду OOLEY провести семинар с практикумом по проработке проектов для ваших сотрудников, клиентов или друзей","link":"https://www.instagram.com/ooley42/"}],"status":"process","list":"ooley-project","type":"project"},"headers":[],"relativePath":"designs/ooley/projectapp/index.md","filePath":"designs/ooley/projectapp/index.md"}'),a={name:"designs/ooley/projectapp/index.md"},p=e("h3",{id:"синтез-множества-подходов-к-реализации-творческих-начинании-с-позиции-психологии-педагогики-и-непосредственнои-практики",tabindex:"-1"},[n("Синтез множества подходов к реализации творческих начинаний с позиций психологии, педагогики и непосредственной практики. "),e("a",{class:"header-anchor",href:"#синтез-множества-подходов-к-реализации-творческих-начинании-с-позиции-психологии-педагогики-и-непосредственнои-практики","aria-label":'Permalink to "Синтез множества подходов к реализации творческих начинаний с позиций психологии, педагогики и непосредственной практики."'},"​")],-1),r=e("p",null,"Изучение творческого процесса как снаружи, так и изнутри показало обнаружило множество закономерностей. Созидание чего-то, руководствуясь собственным чувством прекрасного, происходит по общим принципам вне зависимости от кокретного направления. Даже работая в одиночку люди приходят к схожим выводам относительно внутренней организации творческого процесса. А для продуктивной коллаборации структура просто необходима.",-1),i=e("p",null,"К вопросам организации проектной работы приходят с самых разных сторон: от менеджмента и предпринимательства до дизайна и программирования. Многие современные институты устроены по проектному принципу работы: от строительства до музыкального и видео-продакшна. И каждый исследователь приходит к какой-то своей специфической системе, но с очень схожими задачами.",-1),c=e("p",null,"Оформить деятельность в виде отдельных начинаний с четкими требованиями к результатам и срокам. Разбить сложные цели на более простые и выполнимые задачи, чтобы затем зримо и поступательно двигаться к их достижению. Этого требует само наше естество: объективные когнитивные ограничения человека, а также инстинктивная биохимия нашего мозга.",-1),l=e("p",null,"Состояние потока, возникающее в увлеченном творческой деятельностью человека, становится не только мощным драйвером движения вперед, но и может стать самой целью этого движения. Когда сам процесс творчества становится в радость, человек способен слиться с ним, раствориться в потоке, ощутить себя настоящим творцом. Такое осознанное и прочувствованное творчество — способ удивительных трансформаций личности. Это метод не просто саморазвития, но самоактуализации человека, обнаруживающего свою уникальность и аутентичность.",-1),d=[p,r,i,c,l];function _(h,u,m,x,f,y){return s(),o("div",null,d)}const k=t(a,[["render",_]]);export{g as __pageData,k as default};
