import{_ as t,o as s,c as a,a as e,b as n}from"./chunks/framework.oV3PwDqy.js";const v=JSON.parse('{"title":"ШЛЁП","description":"Разработка и внедрение практик прямого обращения людей друг к другу через использование свободных городских поверхностей","frontmatter":{"title":"ШЛЁП","description":"Разработка и внедрение практик прямого обращения людей друг к другу через использование свободных городских поверхностей","icon":"slap.svg","url":"https://frkt.ru/shlyop","date":"2015-11-19T00:00:00.000Z","status":"development","type":"design"},"headers":[],"relativePath":"designs/slap/index.md","filePath":"designs/slap/index.md"}'),o={name:"designs/slap/index.md"},d=e("h2",{id:"наклеики-и-плакаты-для-города",tabindex:"-1"},[n("Наклейки и плакаты для города "),e("a",{class:"header-anchor",href:"#наклеики-и-плакаты-для-города","aria-label":'Permalink to "Наклейки и плакаты для города"'},"​")],-1),i=e("p",null,"Улицы города — непрекращающиеся потоки самой разнообразной информации. От шума проезжающих машин до вывесок и витрин магазинов, от разговоров прохожих и рекламы до художественно-архитектурных решений застройки. Почему так редко эти потоки соединяют горожан непосредственно друг с другом? Информационное пространство используется практически только компаниями и государством, делая простых горожан лишь потребителями.",-1),l=e("p",null,"Публикация информации в Интернете стала спасительным средством для общественного самовыражения. Однакос етевому открытому общению становится тесно в рамках цифровых устройств и экранов. Многие осознали ценность свободного открытого обмена мыслями, образами, идеями, знаниями, услугами, всякими штуками и, умея делать это онлайн, смогут легко включиться в такие же процессы в оффлайне. Исследования",-1),r=e("p",null,"Мы исследуем и разрабатываем различные практики прямого обращения горожан друг к другу. Например идея внедрения права гражданина на самостоятельную публикацию плакатов, афиш, стенгазет, объявлений, наклеек, рисунков и надписей на свободных городских поверхностях. Это может помочь людям отвоевать своё внимание от государственных и корпоративных информационных каналов и, нарушив монотонность городских поверхностей, создать распределённую городскую информационную среду, наполненную образностью и вдохновением, творчеством и самовыражением каждого, кому есть, чем поделиться с городом и его обитателями. Польза городу и горожанину",-1),c=e("p",null,"Выпустив наши идеи, образы и мысли из темниц экранов на свободные городские поверхности, мы лучше сможем понять друг друга, увидим наши различия и сходства, научимся находить общий выразительный язык. Прямые горизонтальные взаимодействия горожан способны создать распределённую сетевую общественную систему. Использование свободных поверхностей для самостоятельной публикации авторской информации открывает возможности прямого творческого обращения художников к горожанам. Каждый способен стать художником, важно лишь найти то, что хочешь выразить из себя вовне. Приглашение к художественному диалогу способно во многих пробудить внутреннее стремление к творчеству и открытой самореализации. Активное общение и взаимная поддержка горожан способна превратить разобщенную толпу жильцов и работников в сообщество разносторонних деятелей, объединённых распределённой информационной инфраструктурой.",-1),p=[d,i,l,r,c];function _(h,u,m,f,x,g){return s(),a("div",null,p)}const $=t(o,[["render",_]]);export{v as __pageData,$ as default};
