import{_ as t,d as o,o as n,e,J as a}from"./chunks/framework.BoqFi4zQ.js";const $=JSON.parse('{"title":"Развитие","description":"Развитие — движение системы к новому равновесию","frontmatter":{"title":"Развитие","icon":"logo_R.svg","date":"2022-02-26T00:00:00.000Z","description":"Развитие — движение системы к новому равновесию"},"headers":[],"relativePath":"frukt/development/index.md","filePath":"frukt/development/index.md"}'),s={name:"frukt/development/index.md"},d=e("h3",{id:"прогресс-—-бесконечное-движение-вперед-а-развитие-—-движение-к-балансу",tabindex:"-1"},[a("Прогресс — бесконечное движение вперед, а развитие — движение к балансу. "),e("a",{class:"header-anchor",href:"#прогресс-—-бесконечное-движение-вперед-а-развитие-—-движение-к-балансу","aria-label":'Permalink to "Прогресс — бесконечное движение вперед, а развитие — движение к балансу."'},"​")],-1),r=e("p",null,"Развитие — это естественная реакция живой системы на изменения в окружающей среде. Человек, самостоятельно создавая среду вокруг себя, запускает петли обратной связи, в свою очередь влияющие на него самого. Накопленные поколениями способы взаимодействия людей ткут собой ткань всеобщей культуры. Именно культура содержит в себе все возможные способы существования человека в обществе. Передаваемые ранее ценные информационные накопления теперь просто разлиты в пространстве сети и одновременно легко- и труднодоступны для каждого.",-1),i=e("p",null,"Национальные и религиозные культуры продолжают передаваться новым поколениям, но все большую часть образцов поведения люди получают из глобальной сети. Цивилизации пульсируют периодами охранения и переосмысления первооснов. На определенном этапе развития тысячелетиями считавшееся естественным рабство всего за пару столетий стало неприемлемым для глобализующегося общества. С тех пор перемены и трансформации социальных практик только ускоряются. Появляются новые социальные роли и новые способы экономического взаимодействия. Современный человек адаптируется к самым разным условиям окружающей его антропогенной среды.",-1),c=e("p",null,"Каждый человек обладает уникальным даром творчества. Мы не просто приспосабливаемся к реальности, мы активно осмысляем ее, преображаем в соответствии с нашими представлениями о прекрасном. В современном потребительском обществе возможность влиять на мир сильно зависит платежеспособности человека. Но этого недостаточно. Богатые и знаменитые — это всегда меньшинство, а возможности для самореализации ищет практически каждый. Не только обеспечить себя и своих близких, но сделать что-то значимое, ценное, полезное для мира.",-1),l=e("p",null,"Мы создаем среду для сознательного скоординированного перераспределения ресурсов в обществе. Фонд Развития — это инструмент коллективного творчества. Каждая программа Фонда — ядро сообщества людей, объединенных общими идеями и целями преобразования мира. Здесь люди добровольно вкладывают время, энергию и средства в значимое для них дело, проявляя себя и своё видение прекрасного.",-1),_=[d,r,i,c,l];function p(h,m,f,u,x,k){return n(),o("div",null,_)}const g=t(s,[["render",p]]);export{$ as __pageData,g as default};
