import{_ as a,d as n,o as r,e,a5 as o}from"./chunks/framework.wH9i19Ac.js";const u=JSON.parse('{"title":"Развитие","description":"Развитие — движение системы к новому равновесию","frontmatter":{"title":"Развитие","icon":"logo_R.svg","date":"2022-02-26T00:00:00.000Z","description":"Развитие — движение системы к новому равновесию"},"headers":[],"relativePath":"frukt/development/index.md","filePath":"frukt/development/index.md"}'),l={name:"frukt/development/index.md"};function d(s,t,i,p,c,m){return r(),n("div",null,t[0]||(t[0]=[e("h3",{id:"прогресс-—-бесконечное-движение-вперед-а-развитие-—-движение-к-балансу",tabindex:"-1"},[o("Прогресс — бесконечное движение вперед, а развитие — движение к балансу. "),e("a",{class:"header-anchor",href:"#прогресс-—-бесконечное-движение-вперед-а-развитие-—-движение-к-балансу","aria-label":'Permalink to "Прогресс — бесконечное движение вперед, а развитие — движение к балансу."'},"​")],-1),e("p",null,"Развитие — это естественная реакция живой системы на изменения в окружающей среде. Человек, самостоятельно создавая среду вокруг себя, запускает петли обратной связи, в свою очередь влияющие на него самого. Накопленные поколениями способы взаимодействия людей ткут собой ткань всеобщей культуры. Именно культура содержит в себе все возможные способы существования человека в обществе. Передаваемые ранее ценные информационные накопления теперь просто разлиты в пространстве сети и одновременно легко- и труднодоступны для каждого.",-1),e("p",null,"Национальные и религиозные культуры продолжают передаваться новым поколениям, но все большую часть образцов поведения люди получают из глобальной сети. Цивилизации пульсируют периодами охранения и переосмысления первооснов. На определенном этапе развития тысячелетиями считавшееся естественным рабство всего за пару столетий стало неприемлемым для глобализующегося общества. С тех пор перемены и трансформации социальных практик только ускоряются. Появляются новые социальные роли и новые способы экономического взаимодействия. Современный человек адаптируется к самым разным условиям окружающей его антропогенной среды.",-1),e("p",null,"Каждый человек обладает уникальным даром творчества. Мы не просто приспосабливаемся к реальности, мы активно осмысляем ее, преображаем в соответствии с нашими представлениями о прекрасном. В современном потребительском обществе возможность влиять на мир сильно зависит платежеспособности человека. Но этого недостаточно. Богатые и знаменитые — это всегда меньшинство, а возможности для самореализации ищет практически каждый. Не только обеспечить себя и своих близких, но сделать что-то значимое, ценное, полезное для мира.",-1),e("p",null,"Мы создаем среду для сознательного скоординированного перераспределения ресурсов в обществе. Фонд Развития — это инструмент коллективного творчества. Каждая программа Фонда — ядро сообщества людей, объединенных общими идеями и целями преобразования мира. Здесь люди добровольно вкладывают время, энергию и средства в значимое для них дело, проявляя себя и своё видение прекрасного.",-1)]))}const _=a(l,[["render",d]]);export{u as __pageData,_ as default};
