import{_ as e,o as t,c as a,d as r}from"./chunks/framework.58be8ee7.js";const m=JSON.parse('{"title":"Фонд ФРУКТ","description":"Фонд Развития Универсализации, Кооперации и Творчества","frontmatter":{"title":"Фонд ФРУКТ","description":"Фонд Развития Универсализации, Кооперации и Творчества","list":"main","cover":"logo_stencil.svg"},"headers":[],"relativePath":"index.md","filePath":"index.md"}'),s={name:"index.md"},o=r('<p><a href="./frukt/">Фонд ФРУКТ</a> – это неформальная некоммерческая организация, цель которой состоит в развитии универсализации, кооперации и творчества в человеке и обществе.</p><p>С 2012 года мы проводим <a href="./research/">исследования</a>, разрабатываем <a href="./designs/">социо-культурные программы</a> и по возможности занимаемся их реализацией.</p><p>Для этого мы собираем и обобщаем массы как теоретического, так и практического опыта реализации открытых проектов. В ходе наших экспериментов мы накапливаем ценные знания, которыми стремимся делиться с миром. Здесь мы открыто систематизируем эти знания в единой системе. Но это не догма, а скорее отправная точка для тех, кто хотел бы с нами погрузиться в процесс преобразования себя и мира вокруг.</p><p>Чтобы скоординированно двигаться к ясным целям, нам важно правильно понимать друг друга. Для этого мы разрабываем особый язык продуктивного общения людей <a href="./discourse/">Фруктовый дискурс</a>.</p><p>В основе экономической модели развития Фонда лежат принципы добровольного творческого сотрудничества. Осознанный поступок во благо себя и окружающих как единого организма – базовая &quot;фруктовая&quot; транзакция. Это не эквивалетный обмен, это новый подход к древнейшей форме обмена – <a href="./gifts/">экономике дара</a>, где и контекст и личности участвующих в обмене имеют определяющее значение.</p><p>Пока <a href="./people/">мы</a> в самом начале пути и открыты к широкому спектру сотрудничества.</p>',6),_=[o];function i(n,p,c,d,f,l){return t(),a("div",null,_)}const u=e(s,[["render",i]]);export{m as __pageData,u as default};
