import{_ as r}from"./chunks/youtube-embed.BA1KNDL3.js";import{_ as n,d as s,o as i,K as p,f as o,e}from"./chunks/framework.wH9i19Ac.js";const x=JSON.parse('{"title":"Диатаксис","description":"Системный подход к созданию технической документации","frontmatter":{"title":"Диатаксис","description":"Системный подход к созданию технической документации","date":"2022-04-08T00:00:00.000Z","cover":"diataxis.png"},"headers":[],"relativePath":"research/diataxis/index.md","filePath":"research/diataxis/index.md"}'),d={name:"research/diataxis/index.md"};function l(h,t,c,u,m,_){const a=r;return i(),s("div",null,[t[0]||(t[0]=p('<p>Система Диатаксис направлена на решение проблемы структуры в технической документации. Она применяет системный подход к пониманию потребностей пользователей документации в их цикле взаимодействия с продуктом.</p><h2 id="четыре-режима-документации" tabindex="-1">Четыре режима документации <a class="header-anchor" href="#четыре-режима-документации" aria-label="Permalink to &quot;Четыре режима документации&quot;">​</a></h2><blockquote><p><strong><em>Diátaxis</em></strong></p><p>От Древнегреческого δῐᾰ́τᾰξῐς - <em>dia</em>, &quot;через&quot;, and <em>taxis</em>, &quot;расположение&quot;.</p></blockquote><p>Система определяет четыре грани документации: <strong>руководства</strong>, <strong>инструкции</strong>, <strong>справочники</strong> and <strong>объяснения</strong>.</p><p>Каждый из этих режимов (или типов) отвечает на разные потребности пользователя, служит разным целям и требует разных подходов к созданию.</p><p>Техническая документация должна быть структурирована в соответствии с этими четырьмя типами и должна держать их раздельно и обособленно друг от друга.</p><p>Другими словами, то, что мы называем документацией — фундаментально не что-то одно, а сразу четыре. Понимание следствий из этого и того, как эти четыре вещи работают, могут помочь улучшить большую часть документации.</p><p>Диатаксис способен сделать документацию и проекты лучше, а команды, которые работают с ними, более успешными.</p><hr><h2 id="простая-и-легкая-в-освоении" tabindex="-1">Простая и легкая в освоении <a class="header-anchor" href="#простая-и-легкая-в-освоении" aria-label="Permalink to &quot;Простая и легкая в освоении&quot;">​</a></h2><p>Система проста, легка в понимании и незамысловата в применении. Она не накладывает ограничений в реализации.</p><h2 id="полная-и-целостная" tabindex="-1">Полная и целостная <a class="header-anchor" href="#полная-и-целостная" aria-label="Permalink to &quot;Полная и целостная&quot;">​</a></h2><p>Диатаксис предоставляет комплексную и практически универсально-применимую схему, которая была проверена на практике в широком диапазоне направлений и <a href="./adoption/">применений</a>. Они включают и большие и маленькие, и открытые и закрытые проекты документации.</p><h2 id="видео-презентации" tabindex="-1">Видео-презентации <a class="header-anchor" href="#видео-презентации" aria-label="Permalink to &quot;Видео-презентации&quot;">​</a></h2>',14)),o(a,{link:"https://www.youtube.com/watch?v=t4vKPhjcMZg"}),o(a,{link:"https://www.youtube.com/watch?v=710uQqIqsWk"}),t[1]||(t[1]=e("p",null,[e("a",{href:"https://www.writethedocs.org/topics/",target:"_blank",rel:"noreferrer"},"https://www.writethedocs.org/topics/")],-1)),t[2]||(t[2]=e("p",null,[e("a",{href:"https://github.com/DeFUCC/diataxis-documentation-framework",target:"_blank",rel:"noreferrer"},"https://github.com/DeFUCC/diataxis-documentation-framework")],-1))])}const b=n(d,[["render",l]]);export{x as __pageData,b as default};
